mapper.select:
select
id,unt, app,taskid,taskSource,invOutType,wipEntityId,itemCode,quantity,statuscode
from yx_ils_whout


      and   taskid=#{taskId}


      and  wipEntityId=#{wipEntityId}


      and  wipEntityId=#{wipEntityId}

      and  statuscode=#{statuscode}

select id, unt, app, taskid, tasksource, invouttype, locator, shelfcode, wipentityid, stockno, itemcode, quantity, date_format(outtime,'%Y-%m-%d %H:%i') as outtime, statuscode, (case when statuscode = 0 then '任务新建/等待执行' when statuscode = 11 then '任务执行中' when statuscode = 12 then '任务执行完成' when statuscode = 13 then '任务执行异常' when statuscode = 14 then '任务取消' end) AS statusinfor, memoinfo1, memoinfo2 from yx_ils_whout
and taskid=#{taskid}
and wipentityid=#{wipentityid}
and itemcode=#{itemcode}
and UNIX_TIMESTAMP(outtime) between #{begindate}/1000 and #{enddate}/1000
